import React, { useState } from 'react';

function CreatePostPage() {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    location: '',
    cost: '',
    tags: '',
    media: []
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    const files = Array.from(e.target.files);
    if (files.length > 3) {
      alert("Max 3 media files allowed.");
      return;
    }
    setFormData(prev => ({ ...prev, media: files }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Submitting form:', formData);
  };

  return (
    <div className="container d-flex justify-content-center align-items-center" style={{ minHeight: '90vh' }}>
      <div className="card p-4 shadow-sm" style={{ maxWidth: '500px', width: '100%', borderRadius: '20px' }}>
        <h4 className="text-center text-primary mb-3 fw-bold">ðŸ“¸ Share Your Travel Experience</h4>

        <form onSubmit={handleSubmit}>
          {/* Upload Media */}
          <div className="mb-3 text-center">
            <label className="form-label d-block mb-2 fw-semibold">Add Photos or Videos</label>
            <input
              type="file"
              className="form-control rounded-pill"
              accept="image/*,video/*"
              multiple
              onChange={handleFileChange}
            />
            <small className="text-muted">Max 3 files. Videos under 30 sec.</small>
          </div>

          {/* Title */}
          <div className="mb-3">
            <input
              type="text"
              name="title"
              value={formData.title}
              onChange={handleChange}
              className="form-control rounded-pill"
              placeholder="Post title (e.g. Sunset hike)"
              required
            />
          </div>

          {/* Description */}
          <div className="mb-3">
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              className="form-control rounded"
              rows="3"
              placeholder="What's your story?"
              required
            />
          </div>

          {/* Location & Cost */}
          <div className="d-flex gap-2 mb-3">
            <input
              type="text"
              name="location"
              value={formData.location}
              onChange={handleChange}
              className="form-control rounded-pill"
              placeholder="Location"
              required
            />
            <input
              type="number"
              name="cost"
              value={formData.cost}
              onChange={handleChange}
              className="form-control rounded-pill"
              placeholder="Cost ($)"
            />
          </div>

          {/* Tags */}
          <div className="mb-4">
            <input
              type="text"
              name="tags"
              value={formData.tags}
              onChange={handleChange}
              className="form-control rounded-pill"
              placeholder="Tags (e.g. hiking, music)"
            />
          </div>

          {/* Submit Button */}
          <button type="submit" className="btn btn-primary w-100 rounded-pill fw-semibold">
            Share Post
          </button>
        </form>
      </div>
    </div>
  );
}

export default CreatePostPage;
