import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { getAllPosts } from '../services/postService';
import PostCard from '../components/PostCard';

function HomePage() {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    getAllPosts()
      .then(res => setPosts(res.data))
      .catch(console.error);
  }, []);

  return (
    <div className="container py-4">
      {/* Welcome Section */}
      <div className="text-center mb-5">
        <h1 className="fw-bold text-primary">üåç Welcome to Asiru</h1>
        <p className="text-muted">
          Discover, learn, and share skills through unforgettable travel experiences.
        </p>
        <div className="mt-3">
          <Link to="/post/new" className="btn btn-primary me-2">Create Post</Link>
          <Link to="/itinerary/new" className="btn btn-outline-secondary">New Itinerary</Link>
        </div>
      </div>

      {/* Posts Section */}
      <h2 className="mb-3 text-secondary">Latest Skill-Sharing Posts</h2>
      {posts.length === 0 ? (
        <p>No posts yet. Be the first to share your journey! üöÄ</p>
      ) : (
        <div className="row g-4">
          {posts.map(post => (
            <div className="col-sm-6 col-md-4" key={post.id}>
              <PostCard post={post} />
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default HomePage;
