// src/pages/EditPostPage.jsx
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';


export default function EditPostPage() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [form, setForm] = useState({
    title: '',
    description: '',
    // you could load photoPaths/videoPath if you want to show previews
  });

  useEffect(() => {
    // load post data
    axios.get(`http://localhost:8000/api/posts/${id}`)
      .then(res => setForm(res.data))
      .catch(console.error);
  }, [id]);

  const handleChange = e => {
    const { name, value } = e.target;
    setForm(f => ({ ...f, [name]: value }));
  };

  const handleSubmit = e => {
    e.preventDefault();
    // if you want to update media too, you need FormData and multipart/form-data; here we update text only
    axios.put(`http://localhost:8000/api/posts/${id}`, form)
      .then(() => {
        alert('Post updated!');
        navigate('/');
      })
      .catch(err => {
        console.error(err);
        alert('Update failed');
      });
  };

  return (
    <div className="container py-5">
      <h2>Edit Post</h2>
      <form onSubmit={handleSubmit} className="mt-4">
        <div className="mb-3">
          <label className="form-label">Title</label>
          <input
            name="title"
            value={form.title}
            onChange={handleChange}
            required
            className="form-control"
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Description</label>
          <textarea
            name="description"
            value={form.description}
            onChange={handleChange}
            rows="4"
            required
            className="form-control"
          />
        </div>
        <button type="submit" className="btn btn-success">Save Changes</button>
      </form>
    </div>
  );
}
