import React, { useState } from 'react';

function CreatePostPage() {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    location: '',
    cost: '',
    tags: '',
    media: []
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    const files = Array.from(e.target.files);
    if (files.length > 3) {
      alert("Only 3 files allowed.");
      return;
    }
    setFormData(prev => ({ ...prev, media: files }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Post submitted:', formData);
  };

  return (
    <div className="d-flex justify-content-center align-items-center" style={{ minHeight: '100vh', background: '#0f0f0f' }}>
      <div className="p-5 shadow-lg" style={{
        maxWidth: '600px',
        width: '100%',
        borderRadius: '20px',
        background: 'rgba(255, 255, 255, 0.05)',
        backdropFilter: 'blur(15px)',
        border: '1px solid rgba(255, 255, 255, 0.1)',
        color: '#fff'
      }}>
        <h3 className="text-center mb-4" style={{ fontWeight: 600, letterSpacing: '0.5px' }}>🚀 Create New Post</h3>

        <form onSubmit={handleSubmit}>
          {/* Media Upload */}
          <div className="mb-4">
            <label className="form-label">Upload Media (Images or Videos)</label>
            <input
              type="file"
              className="form-control bg-dark border-secondary text-white"
              accept="image/*,video/*"
              multiple
              onChange={handleFileChange}
            />
            <small className="text-muted">Max 3 files. Videos under 30 seconds.</small>
          </div>

          {/* Title */}
          <div className="form-floating mb-3">
            <input
              type="text"
              className="form-control bg-dark text-white border-secondary"
              id="title"
              name="title"
              placeholder="Title"
              value={formData.title}
              onChange={handleChange}
              required
            />
            <label htmlFor="title">Post Title</label>
          </div>

          {/* Description */}
          <div className="form-floating mb-3">
            <textarea
              className="form-control bg-dark text-white border-secondary"
              placeholder="Description"
              id="description"
              name="description"
              value={formData.description}
              onChange={handleChange}
              style={{ height: '100px' }}
              required
            />
            <label htmlFor="description">Description</label>
          </div>

          {/* Location & Cost */}
          <div className="row mb-3">
            <div className="col">
              <div className="form-floating">
                <input
                  type="text"
                  className="form-control bg-dark text-white border-secondary"
                  id="location"
                  name="location"
                  placeholder="Location"
                  value={formData.location}
                  onChange={handleChange}
                  required
                />
                <label htmlFor="location">Location</label>
              </div>
            </div>
            <div className="col">
              <div className="form-floating">
                <input
                  type="number"
                  className="form-control bg-dark text-white border-secondary"
                  id="cost"
                  name="cost"
                  placeholder="Cost"
                  value={formData.cost}
                  onChange={handleChange}
                />
                <label htmlFor="cost">Trip Cost ($)</label>
              </div>
            </div>
          </div>

          {/* Tags */}
          <div className="form-floating mb-4">
            <input
              type="text"
              className="form-control bg-dark text-white border-secondary"
              id="tags"
              name="tags"
              placeholder="Tags"
              value={formData.tags}
              onChange={handleChange}
            />
            <label htmlFor="tags">Tags (comma separated)</label>
          </div>

          {/* Submit Button */}
          <button
            type="submit"
            className="btn w-100"
            style={{
              background: 'linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)',
              border: 'none',
              color: '#fff',
              fontWeight: 'bold',
              borderRadius: '50px',
              padding: '10px 20px'
            }}
          >
            ✨ Share Your Journey
          </button>
        </form>
      </div>
    </div>
  );
}

export default CreatePostPage;
