// src/pages/EditPostPage.js

import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
<Link to={`/edit/${post.id}`} className="btn btn-outline-warning w-100 mt-2 mb-2">
  Edit
</Link>

function EditPostPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    photoPaths: [],
    videoPath: ''
  });

  useEffect(() => {
    axios.get(`http://localhost:8000/api/posts/${id}`)
      .then(res => setFormData(res.data))
      .catch(err => console.error('Error loading post:', err));
  }, [id]);

  const handleChange = e => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:8000/api/posts/${id}`, formData);
      alert('Post updated successfully!');
      navigate('/');
    } catch (error) {
      console.error('Failed to update post:', error);
      alert('Error updating post.');
    }
  };

  return (
    <div className="container py-5">
      <div className="row justify-content-center">
        <div className="col-md-8 col-lg-7">
          <div className="card shadow-sm border-0">
            <div className="card-body p-4">
              <h3 className="card-title mb-4 text-primary">Edit Post</h3>
              <form onSubmit={handleSubmit}>
                <div className="mb-3">
                  <label className="form-label">Post Title</label>
                  <input
                    type="text"
                    name="title"
                    value={formData.title}
                    onChange={handleChange}
                    className="form-control"
                    required
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Description</label>
                  <textarea
                    name="description"
                    value={formData.description}
                    onChange={handleChange}
                    rows="4"
                    className="form-control"
                    required
                  />
                </div>
                <button type="submit" className="btn btn-success w-100">
                  Update Post
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default EditPostPage;
