import React, { useState } from 'react';

function CreatePostPage() {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    location: '',
    cost: '',
    tags: '',
    media: []
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    const files = Array.from(e.target.files);
    if (files.length > 3) {
      alert("You can upload up to 3 files only.");
      return;
    }
    setFormData(prev => ({ ...prev, media: files }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // For now, we'll just log the data
    console.log('Submitting post:', formData);
  };

  return (
    <div className="container py-4">
      <h2 className="mb-4 text-primary fw-bold">Create a New Travel Skill Post</h2>
      <p className="text-muted mb-4">Share a moment, a tip, or a skill from your journey with others.</p>

      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="form-label">Post Title</label>
          <input type="text" className="form-control" name="title" value={formData.title} onChange={handleChange} required />
        </div>

        <div className="mb-3">
          <label className="form-label">Short Description</label>
          <textarea className="form-control" name="description" value={formData.description} onChange={handleChange} rows="3" required />
        </div>

        <div className="mb-3">
          <label className="form-label">Location</label>
          <input type="text" className="form-control" name="location" value={formData.location} onChange={handleChange} required />
        </div>

        <div className="mb-3">
          <label className="form-label">Trip Cost (USD)</label>
          <input type="number" className="form-control" name="cost" value={formData.cost} onChange={handleChange} />
        </div>

        <div className="mb-3">
          <label className="form-label">Tags (comma separated)</label>
          <input type="text" className="form-control" name="tags" value={formData.tags} onChange={handleChange} />
        </div>

        <div className="mb-3">
          <label className="form-label">Upload Images or Short Videos (Max 3 files, 30 sec each)</label>
          <input type="file" className="form-control" accept="image/*,video/*" multiple onChange={handleFileChange} />
        </div>

        <button type="submit" className="btn btn-primary">Submit Post</button>
      </form>
    </div>
  );
}

export default CreatePostPage;
