import React, { useState } from 'react';

function CreatePostPage() {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    location: '',
    cost: '',
    tags: '',
    media: []
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    const files = Array.from(e.target.files);
    if (files.length > 3) {
      alert("You can upload up to 3 files only.");
      return;
    }
    setFormData(prev => ({ ...prev, media: files }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Submit logic goes here
    console.log('Form submitted:', formData);
  };

  return (
    <div className="container py-5">
      <div className="row justify-content-center">
        <div className="col-md-8 col-lg-7">
          <div className="card shadow-sm border-0">
            <div className="card-body p-4">
              <h3 className="card-title mb-4 text-primary">Create a New Travel Skill Post</h3>
              <p className="text-muted mb-4">
                Share a unique travel skill, a memorable moment, or helpful tips from your journey.
              </p>

              <form onSubmit={handleSubmit}>
                <div className="mb-3">
                  <label className="form-label">Post Title</label>
                  <input
                    type="text"
                    className="form-control"
                    name="title"
                    value={formData.title}
                    onChange={handleChange}
                    placeholder="e.g. Surfing in Bali"
                    required
                  />
                </div>

                <div className="mb-3">
                  <label className="form-label">Short Description</label>
                  <textarea
                    className="form-control"
                    name="description"
                    rows="3"
                    value={formData.description}
                    onChange={handleChange}
                    placeholder="Tell us what you learned, taught, or experienced..."
                    required
                  />
                </div>

                <div className="row">
                  <div className="col-md-6 mb-3">
                    <label className="form-label">Location</label>
                    <input
                      type="text"
                      className="form-control"
                      name="location"
                      value={formData.location}
                      onChange={handleChange}
                      placeholder="City, Country"
                      required
                    />
                  </div>

                  <div className="col-md-6 mb-3">
                    <label className="form-label">Trip Cost (USD)</label>
                    <input
                      type="number"
                      className="form-control"
                      name="cost"
                      value={formData.cost}
                      onChange={handleChange}
                      placeholder="e.g. 150"
                    />
                  </div>
                </div>

                <div className="mb-3">
                  <label className="form-label">Tags (comma separated)</label>
                  <input
                    type="text"
                    className="form-control"
                    name="tags"
                    value={formData.tags}
                    onChange={handleChange}
                    placeholder="e.g. hiking, cooking, music"
                  />
                </div>

                <div className="mb-4">
                  <label className="form-label">Upload Images or Short Videos (Max 3 files)</label>
                  <input
                    type="file"
                    className="form-control"
                    accept="image/*,video/*"
                    multiple
                    onChange={handleFileChange}
                  />
                  <small className="form-text text-muted">
                    Max 3 files. Videos must be under 30 seconds.
                  </small>
                </div>

                <div className="d-grid">
                  <button type="submit" className="btn btn-primary">
                    Submit Post
                  </button>
                </div>
              </form>

            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default CreatePostPage;
